mapper.select:
SELECT post_id
FROM post
WHERE party_no = #{value}
AND is_deleted = 0
ORDER BY post_no DESC;

mapper.update:
UPDATE post
SET 
	post_title = #{title},
	post_content = #{content},
	post_read_cnt = #{readCnt},
	post_comment_cnt = #{commentCnt},
	is_deleted = #{deleted}
	post_is_notification = #{notification}
WHERE
	post_id = #{id}
